import PropTypes from 'prop-types';

const Profile = (props) =>{

  function handleName(){
    console.log(props.fullName);
    alert(props.fullName);
  }

  return (
    <div onMouseEnter={handleName} >
    <div class= "row mt-4">
      <div class="col-2">
       <img onMouseEnter={handleName} class=" ms-4" style={{width:'160px',height:'180px'}} src={props.children} alt="img"/>
      </div>
      <div class="col-6 ps-0 fs-5">
          <b style={{color:"#2c7996", textDecoration:'underline'}}>Full Name : </b><span>{props.fullName}</span><br/>
          <b style={{color:"#2c7996"}}>Bio : </b><span>{props.bio}</span><br/>
          <b style={{color:"#2c7996"}}>Profession : </b><span>{props.profession}</span>
      </div>  
    </div>
    </div>

  ) 
}
Profile.defaultProps={
 fullName : "full name",
 bio : "bio",
 profession : "profession"
}

Profile.propTypes={ 
    fullName: PropTypes.string.isRequired,   
    bio: PropTypes.string.isRequired,
    profession: PropTypes.string.isRequired
}

export default Profile
