import PropTypes from 'prop-types';

const Profile = (props) =>{
  function handleName(){
    {props.fullName}
  }
  return (
    <div class= "row mt-3">
      <div class="col-2 pe-0">
       <img onHover={handleName} class=" ms-4" style={{width:'160px',height:'180px'}} src={props.children} alt="img"/>
      </div>
      <div class="col-6 ps-0 fs-5">
          <b>Full Name : </b><span>{props.fullName}</span><br/>
          <b>Bio : </b><span>{props.bio}</span><br/>
          <b>Profession : </b><span>{props.profession}</span>
      </div>  
    </div>


  ) 
}
Profile.defaultProps={
 fullName : "full name",
 bio : "bio",
 profession : "profession"
}

Profile.propTypes={ 
    fullName: PropTypes.string.isRequired,   
    bio: PropTypes.string.isRequired,
    profession: PropTypes.string.isRequired
}

export default Profile
